import React from "react";
// import AdminSidebar from "../../../Components/";
import AdminSidebar from "../../../Components/AdminSidebar";
import "./AdminUserDetailsPage.css";
import axios from "axios";
import { useParams } from "react-router-dom";

const AdminUserDetailsPage = () => {
  const [name, setName] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [phone, setPhone] = React.useState("");
  const [isAdminAddProduct, setIsAdminAddProduct] = React.useState(false);
  const [isAdminOrderViewer, setIsAdminOrderViewer] = React.useState(false);
  const [password, setPassword] = React.useState(null);
  const [userOrders, setUserOrders] = React.useState([]);

  const { id } = useParams();

  React.useEffect(() => {
    const { token } = JSON.parse(localStorage.getItem("userData"));

    axios
      .get(`/users/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(({ data }) => {
        setName(data.user.name);
        setIsAdminAddProduct(data.user.isAdminAddProduct);
        setIsAdminOrderViewer(data.user.isAdminAddProduct);
        setEmail(data.user.email);
        setPhone(data.user.phone);
      });
  }, [id]);

  React.useEffect(() => {
    const { token } = JSON.parse(localStorage.getItem("userData"));

    axios
      .get(`/orders/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(({ data }) => {
        const orders = data.flatMap((item) => {
          const newOrder = item.products.map((el) => {
            return { ...el, date: item.createdAt };
          });
          return newOrder;
        });

        setUserOrders(orders);
      });
  }, [id]);

  const submitHanler = (e) => {
    e.preventDefault();
    const { token } = JSON.parse(localStorage.getItem("userData"));

    axios
      .patch(
        `/users/${id}`,
        password
          ? { name, email, phone, isAdminAddProduct, isAdminOrderViewer }
          : { name, email, phone, isAdminAddProduct, isAdminOrderViewer },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      )
      .then((res) => {
        window.M.toast({ html: "Имзменения сохранени", classes: "loginToast" });
      })
      .catch((e) => {
        window.M.toast({
          html: "Что то прошлоо не так",
          classes: "loginToastRed",
        });
      });
  };

  return (
    <div className="adminPage">
      <AdminSidebar />
      <div className="adminUsersDetailsPage">
        <h4 className="center-align">Данные ползователя</h4>
        <div className="row">
          <form className="col m6 offset-m3" onSubmit={(e) => submitHanler(e)}>
            <div className="row">
              <div className="input-field col s12">
                <input
                  id="name"
                  type="text"
                  className="validate"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
                <label htmlFor="name">Имя</label>
              </div>
            </div>
            <div className="row">
              <div className="input-field col s12">
                <input
                  id="email"
                  type="email"
                  className="validate"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
                <label htmlFor="email">Email адрес</label>
              </div>
            </div>
            <div className="row">
              <div className="input-field col s12">
                <input
                  id="phone"
                  type="number"
                  className="validate"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                />
                <label htmlFor="phone">Тел. номер</label>
              </div>
            </div>

            <div className="row">
              <div className="col s4">
                <p>
                  <label>
                    <input
                      value={
                        !isAdminAddProduct && !isAdminOrderViewer ? true : false
                      }
                      checked={
                        !isAdminAddProduct && !isAdminOrderViewer ? true : false
                      }
                      onChange={(e) => {
                        setIsAdminAddProduct(false);
                        setIsAdminOrderViewer(false);
                      }}
                      className="with-gap"
                      name="group1"
                      type="radio"
                    />
                    <span>Обычный ползователь</span>
                  </label>
                </p>
              </div>
              <div className="col s4">
                <p>
                  <label>
                    <input
                      value={isAdminAddProduct}
                      checked={isAdminAddProduct}
                      className="with-gap"
                      name="group1"
                      onChange={(e) => setIsAdminAddProduct(e.target.value)}
                      type="radio"
                    />
                    <span>Админ добавить товар</span>
                  </label>
                </p>
              </div>
              <div className="col s4">
                <p>
                  <label>
                    <input
                      className="with-gap"
                      value={isAdminOrderViewer}
                      checked={isAdminOrderViewer}
                      onChange={(e) => setIsAdminOrderViewer(e.target.value)}
                      name="group1"
                      type="radio"
                    />
                    <span>Админ работает заказамы</span>
                  </label>
                </p>
              </div>
            </div>

            <div className="row">
              <div className="input-field col s12">
                <input
                  id="password"
                  type="password"
                  className="validate"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
                <label htmlFor="password">Пароль</label>
              </div>
            </div>

            {/* <div className="row">
              <div className="input-field col s12">
                <select
                  onChange={(e) => {
                    if (e.target.value === "admin2") {
