import React from "react";
import axios from "axios";
import { useLocation, useParams } from "react-router-dom";
import "./CatalogProduct.css";
import CatalofProductItem from "../Components/CatalogProductItem";
import Loading from "../Components/Loading/Loading";

const CatalogProduct = () => {
  const [category, setCategory] = React.useState({});
  const [subCategories, setSubCategories] = React.useState([]);

  const { id } = useParams();

  const location = useLocation();

  const useQuery = () => new URLSearchParams(location.search);

  let query = useQuery();

  React.useEffect(() => {
    axios.get(`/category/${id}`).then(({ data }) => {
      console.log(data);
      setCategory(data);
    });
  }, [id]);

  React.useEffect(() => {
    axios.get("/subcategory").then((res) => {
      console.log(res.data);

      setSubCategories(res.data);
    });
  }, [category]);

  return (
    <div className="container">
      <h1 className="product-content-title">{query.get("q")}</h1>
      <div className="row mb-3">
        <h3>{category.categoryName}</h3>
        {subCategories.length > 0 &&
          subCategories.map((el) => (
            <div key={el._id} className="subcategory">
              <img width={"70px"} src={`/${el.img.path}`} alt="" />
              <div className="subcategory-img"></div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default CatalogProduct;
