import React from "react";
import "./Basket.css";
import BasketOrderItem from "../Components/BasketOrderItem";
import axios from "axios";
import { baseUrl } from "../baseUrl/baseUrl";
import Loading from "../Components/Loading/Loading";

const Basket = () => {
  const [active, setActive] = React.useState("");
  const [orders, setOrders] = React.useState([]);
  const [entityOrders, setEntityOrders] = React.useState({
    userId: "",
    products: [],
    entity: {
      stir: "",
      entityName: "",
    },
    addressinfo: {
      city: "",
      street: "",
    },
  });

  const [userOrder, setUserOrder] = React.useState({
    userId: "",
    products: [],
    addressinfo: {
      city: "",
      street: "",
    },
  });

  //select option function
  document.addEventListener("DOMContentLoaded", function () {
    var elems = document.querySelectorAll("select");
    window.M.FormSelect.init(elems);
  });

  const radioHandler = (e) => {
    setActive(e.target.value);
  };

  React.useEffect(() => {
    const basket = JSON.parse(localStorage.getItem("salamBasket"));

    axios
      .post(
        baseUrl + "/products/cart",
        { products: basket },
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      )
      .then((res) => {
        setOrders(res.data);
      });
  }, []);

  return (
    <div className="container">
      <h1>Корзина</h1>
      <div className="basket row ">
        <div className="products col s12 l8">
          <div className="row">
            <div className="col s12 m6 l6">
              <label
                className={`choose__option mr-1 ${
                  active === "Доставка" ? "active" : ""
                }`}
              >
                <input
                  className="with-gap"
                  name="group1"
                  type="radio"
                  value={"Доставка"}
                  onChange={(e) => radioHandler(e)}
                />
                <span>Доставка</span>
              </label>
            </div>
            <div className="col s12 m6 l6">
              <label
                className={`choose__option ${
                  active === "Самовывоз" ? "active" : ""
                }`}
              >
                <input
                  className="with-gap"
                  name="group1"
                  type="radio"
                  value={"Самовывоз"}
                  onChange={(e) => radioHandler(e)}
                />
                <span>Самовывоз</span>
              </label>
            </div>
          </div>

          <div className="basket-orders">
            {!orders.length ? (
              <Loading />
            ) : (
              orders.map((order, index) => (
                <BasketOrderItem data={order} key={index} />
              ))
            )}
          </div>
        </div>

        <div className="product-order col s12  l4">
          <div className="form row">
            <div className="form-price">
              <span className="form-price-title">
                Товары <sup>(2)</sup>{" "}
              </span>
              <span className="form-price-number">100000 Р</span>
            </div>
            <div className="form-summ">
              <span className="form-summ-title">Итого</span>
              <span className="form-summ-number">100000 Р</span>
            </div>
            <div className="form-action">
              <button className="btn form-btn">Оформить заказ</button>
            </div>
          </div>

          <form className="form row">
            <form class="col s12">
              <div className="row">
                <p>
                  <label>
                    <input className="with-gap" name="group1" type="radio" />
                    <span>Я физическое лицо</span>
                  </label>
                </p>

                <p>
                  <label>
                    <input className="with-gap" name="group1" type="radio" />
                    <span>Я юридическое лицо</span>
                  </label>
                </p>
              </div>

              <div className="row">
                <div className="input-field col s12">
                  <input id="stir" type="text" className="validate" />
                  <label htmlFor="stir">ИНН организации</label>
                </div>
              </div>

              <div className="row">
                <div className="input-field col s12">
                  <input id="entityName" type="text" className="validate" />
                  <label htmlFor="entityName">Название организации</label>
                </div>
              </div>

              <div className="row">
                <div className="input-field col s12">
                  <input id="email" type="email" className="validate" />
                  <label htmlFor="email">Email</label>
                </div>
              </div>

              <div className="row">
                <div class="input-field col s12">
                  <select>
                    <option value="" disabled selected>
                      Город
                    </option>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="3">3</option>
                    <option value="3">3</option>
                    <option value="3">3</option>
                    <option value="3">3</option>
                    <option value="3">3</option>
                    <option value="3">3</option>
                    <option value="3">3</option>
                    <option value="3">3</option>
                    <option value="3">3</option>
                    <option value="3">3</option>
                  </select>
                </div>
              </div>

              <div className="row">
                <div className="input-field col s12">
                  <input id="email" type="email" className="validate" />
                  <label htmlFor="email">Email</label>
                </div>
              </div>
            </form>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Basket;
