import React from "react";
import { Link } from "react-router-dom";

const CatalogProductItem = ({ data, ownstyle }) => {
  const basketHandler = (e) => {
    let addedProduct = JSON.parse(localStorage.getItem("salamBasket"));

    if (!addedProduct) {
      localStorage.setItem(
        "salamBasket",
        JSON.stringify([
          {
            id: data._id,
            quantity: 1,
            price: data.sellPrice,
            summ: this.quantity * this.price,
          },
        ])
      );
      window.M.toast({ html: "Продукт добавлен", classes: "loginToast" });
    } else {
      const productIds = addedProduct.map((item) => item.id);
      if (addedProduct && productIds.includes(data._id)) {
        window.M.toast({
          html: "Продукт уже существует в ввшем корзине",
          classes: "loginToastYellow",
        });
        return;
      } else if (addedProduct && !productIds.includes(data._id)) {
        addedProduct.push({
          id: data._id,
          quantity: 1,
          price: data.sellPrice,
          summ: this.quantity * this.price,
        });
        localStorage.setItem("salamBasket", JSON.stringify(addedProduct));
        window.M.toast({ html: "Продукт добавлен", classes: "loginToast" });
      }
    }

    // if (addedProduct) {
    //   const productIds = addedProduct.map((item) => item.id);
    //   if (addedProduct && productIds.includes(data._id)) {
    //     window.M.toast({
    //       html: "Продукт уже существует в ввшем корзине",
    //       classes: "loginToastYellow",
    //     });
    //   } else if (addedProduct && !addedProduct.includes(data._id)) {
    //     addedProduct.push({ id: data._id, quantity: 1 });
    //     localStorage.setItem("salamBasket", JSON.stringify(addedProduct));
    //     window.M.toast({ html: "Продукт добавлен", classes: "loginToast" });
    //   } else if (!addedProduct) {
    //     localStorage.setItem(
    //       "salamBasket",
    //       JSON.stringify([{ id: data._id, quantity: 1 }])
    //     );
    //     window.M.toast({ html: "Продукт добавлен", classes: "loginToast" });
    //   }
    // }
  };

  return (
    <div className="col s12 m4" style={ownstyle && ownstyle}>
      <div className="card">
        <div className="card-image">
          <img
            src="https://materializecss.com/images/sample-1.jpg"
            alt="product img"
          />
          <Link
            to={`/product/${data._id}`}
            className="card-title"
            style={{ cursor: "pointer" }}
          >
            {data.title}
          </Link>
        </div>
        <div className="card-content">
          <p>
            I am a very simple card. I am good at containing small bits of
            information. I am convenient because I require little markup to use
            effectively.
          </p>
        </div>
        <div className="card-action">
          <button
            className="btn waves-effect"
            onClick={(e) => basketHandler(e)}
          >
            В корзину
          </button>
        </div>
      </div>
    </div>
  );
};

export default CatalogProductItem;
