import React, { useEffect, useState } from "react";
import axios from "axios";
import CatalogItem from "../Components/CatalogItem";
import "./Catalog.css";
import Loading from "../Components/Loading/Loading";

//Канцтовары , хозтовары , мебель , парфюмерия , бытовые техники , компьютеры, телефоны , мода, автоаксессуары , спорттовары

const Catalog = () => {
  const [catalog, setCatalog] = useState(null);
  const [category, setCategory] = useState(null);
  const [subCategory, setSubCategory] = useState(null);
  const [subSubCategory, setSubSubCategory] = useState(null);

  useEffect(() => {
    fetch("/products")
      .then((res) => res.json())
      .then((data) => {
        const dataCategory = data.map((datum) => datum.category);

        const filteredCat = new Set(Array.from(dataCategory));

        setCatalog([...filteredCat]);
      });
  }, []);

  useEffect(() => {
    axios.get("/category").then((res) => {
      console.log(res.data);
    });
  }, []);

  useEffect(() => {
    axios.get("/subcategory").then((res) => {
      console.log(res.data);
    });
  }, []);

  return (
    <div className="container mb-3">
      <h1>Каталог</h1>
      <div className="catalog">
        {/* {catalog === null ? (
          <Loading />
        ) : (
          catalog.map((item, index) => <CatalogItem key={index} item={item} />)
        )} */}
        <div className="catalog__item">
          <div className="catalog__item-img">
            <img
              src="/uploads/2022-02-13T09:53:17.750Zphoto_2021-02-02_00-43-21.jpg"
              alt=""
            />
          </div>
          <div className="catalog__item-content">
            <h5>category</h5>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Catalog;
