import React, { Component } from "react";
import Slider from "react-slick";

export default class AutoPlayMethods extends Component {
  constructor(props) {
    super(props);
    this.play = this.play.bind(this);
    this.pause = this.pause.bind(this);
  }
  play() {
    this.slider.slickPlay();
  }
  pause() {
    this.slider.slickPause();
  }
  render() {
    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 2000,
    };
    return (
      <div
        className="mb-3"
        style={{ borderRadius: "10px", overflow: "hidden" }}
      >
        <Slider ref={(slider) => (this.slider = slider)} {...settings}>
          <div>
            <img
              style={{ width: "100%" }}
              src="https://react-responsive-carousel.js.org/assets/2.jpeg"
              alt=""
            />
          </div>
          <div>
            <img
              style={{ width: "100%" }}
              src="https://react-responsive-carousel.js.org/assets/2.jpeg"
              alt=""
            />
          </div>
          <div>
            <img
              style={{ width: "100%" }}
              src="https://react-responsive-carousel.js.org/assets/2.jpeg"
              alt=""
            />
          </div>
          <div>
            <img
              style={{ width: "100%" }}
              src="https://react-responsive-carousel.js.org/assets/2.jpeg"
              alt=""
            />
          </div>
          <div>
            <img
              style={{ width: "100%" }}
              src="https://react-responsive-carousel.js.org/assets/2.jpeg"
              alt=""
            />
          </div>
          <div>
            <img
              style={{ width: "100%" }}
              src="https://react-responsive-carousel.js.org/assets/2.jpeg"
              alt=""
            />
          </div>
        </Slider>
      </div>
    );
  }
}
