import React from "react";
import "./BasketOrderItem.css";

const BasketOrderItem = ({ data }) => {
  const deleteHandler = (e) => {
    let basket = JSON.parse(localStorage.getItem("salamBasket"));

    if (basket) {
      const filter = basket.filter((item) => item.id !== data._id);
      localStorage.setItem("salamBasket", JSON.stringify(filte));
    }
  };

  return (
    <div className="basket-order mb-2">
      <div className="order-image">
        <img
          alt="product img"
          src="https://res.cloudinary.com/lmru/image/upload/f_auto,q_auto,w_400,h_400,c_pad,b_white,d_photoiscoming.png/LMCode/93896071_04.jpg"
        />
      </div>
      <div className="order-title">
        <p>{data.title}</p>
      </div>
      <div className="order-quantity row">
        <input
          min="0"
          style={{ width: "40px" }}
          type={"number"}
          defaultValue={"1"}
        />
        <label>шт.</label>
      </div>
      <div className="order-price">
        <h4>{data.sellPrice}$</h4>
      </div>
      <div onClick={(e) => deleteHandler(e)} className="order-delete">
        <i className="fas fa-trash-alt delete"></i>
      </div>
    </div>
  );
};

export default BasketOrderItem;
