import React from "react";
import axios from "axios";
import { useLocation, useParams, Link } from "react-router-dom";
import "./CatalogProduct.css";
import Loading from "../Components/Loading/Loading";

const CatalogProduct = () => {
  const [category, setCategory] = React.useState({});
  const [subCategories, setSubCategories] = React.useState([]);
  const [subSubCategories, setSubSubCategories] = React.useState([]);

  const { id } = useParams();

  const location = useLocation();

  const useQuery = () => new URLSearchParams(location.search);

  let query = useQuery();

  const subcategory = query.get("subcategories");

  React.useEffect(() => {
    axios.get(`/category/${id}`).then(({ data }) => {
      setCategory(data);
    });
  }, [id]);

  React.useEffect(() => {
    axios.get("/subcategory").then((res) => {
      setSubCategories(res.data);
    });
  }, [category]);

  React.useEffect(() => {
    axios.get("/subsubcategory").then((res) => {
      setSubSubCategories(res.data);
    });
  }, []);

  console.log();

  return (
    <div className="container">
      {subCategories.length === 0 && <Loading />}
      <div className="row mb-3">
        {subcategory ? (
          <>
            <Link
              style={{
                display: "flex",
                width: "80px",
                justifyContent: "space-between",
                alignItems: "center",
              }}
              className="mt-3"
              to={location.pathname}
            >
              <i className="small material-icons">chevron_left</i>
              Назад
            </Link>
            <h3>
              {subCategories.length > 0 &&
                subCategories.find((el) => el._id === subcategory)
                  .subCategoryName}
              {console.log(
                subSubCategories.length &&
                  subSubCategories.filter(
                    (el) =>
                      el.subCategory ===
                      subCategories.find((el) => el._id === subcategory)
                        .subCategoryName
                  )
              )}
            </h3>
          </>
        ) : (
          <>
            <Link
              style={{
                display: "flex",
                width: "80px",
                justifyContent: "space-between",
                alignItems: "center",
              }}
              className="mt-3"
              to={"/catalog"}
            >
              <i className="small material-icons">chevron_left</i>
              Назад
            </Link>
            <h3>{category.categoryName}</h3>
            <div className="subcategory-box">
              {subCategories.length > 0 &&
                category.categoryName &&
                subCategories
                  .filter((el) => el.category === category.categoryName)
                  .map((el) => (
                    <div key={el._id} className="subcategory">
                      <div
                        className="subcategory-img"
                        style={{ width: "100px" }}
                      >
                        <img width={"70px"} src={`/${el.img.path}`} alt="" />
                      </div>
                      <div className="subcategory-content">
                        <Link
                          to={`${location.pathname}?subcategories=${el._id}`}
                        >
                          <span
                            className="link__hover"
                            style={{ color: "#000", fontSize: "18px" }}
                          >
                            {el.subCategoryName}
                          </span>
                        </Link>
                        {subSubCategories.length > 0 &&
                          subSubCategories
                            .filter(
                              (elem) => elem.subCategory === el.subCategoryName
                            )
                            .slice(0, 3)
                            .map((item) => (
                              <span> {item.subSubCategoryName} </span>
                            ))}
                      </div>
                    </div>
                  ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default CatalogProduct;
