import React, { useState, useEffect } from "react";
import "./Product.css";
import { CarouselProvider, Slider, Slide, Dot } from "pure-react-carousel";
import "pure-react-carousel/dist/react-carousel.es.css";
import axios from "axios";
import { baseUrlForImg } from "../baseUrl/baseUrl";
import { baseUrl } from "../baseUrl/baseUrl";
import { useParams } from "react-router-dom";

const Product = () => {
  const [product, setProduct] = useState(null);
  const [slidenum, setSlidenum] = useState(null);

  const params = useParams();

  useEffect(() => {
    axios.get(baseUrl + `/products/${params.id}`).then((res) => {
      setProduct(res.data);
      setSlidenum(res.data.img.length);
    });
  }, []);

  const basketHandler = (e) => {
    let addedProduct = JSON.parse(localStorage.getItem("salamBasket"));

    if (!addedProduct) {
      localStorage.setItem(
        "salamBasket",
        JSON.stringify([
          {
            productId: product._id,
            quantity: 1,
            price: product.sellPrice,
          },
        ])
      );
      window.M.toast({ html: "Продукт добавлен", classes: "loginToast" });
      return;
    } else {
      const productIds = addedProduct.map((item) => item.productId);
      if (addedProduct && productIds.includes(product._id)) {
        window.M.toast({
          html: "Продукт уже существует в ввшем корзине",
          classes: "loginToastYellow",
        });
        return;
      } else if (addedProduct && !productIds.includes(product._id)) {
        addedProduct.push({
          productId: product._id,
          quantity: 1,
          price: product.sellPrice,
        });
        localStorage.setItem("salamBasket", JSON.stringify(addedProduct));
        window.M.toast({ html: "Продукт добавлен", classes: "loginToast" });
      }
    }
  };

  return (
    <div className="container mt-3 mb-3">
      <div className="products row">
        <div className="product__image col s12 m6 l7">
          <CarouselProvider
            naturalSlideWidth={100}
            naturalSlideHeight={125}
            totalSlides={slidenum}
            style={{ height: "100%" }}
          >
            <div
              className="col s2"
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
            >
              {product &&
                product.img.map((item, index) => {
                  return (
                    <div className="row">
                      <Dot slide={index} className="slide-dot">
                        <img
                          src={baseUrlForImg + item.path}
                          alt="productImage"
                          className="productImg"
                        />
                      </Dot>
                    </div>
                  );
                })}
            </div>
            <div className="col s10">
              <Slider>
                {product &&
                  product.img.map((item, index) => (
                    <Slide index={index}>
                      <div
                        style={{
                          display: "flex",
                          justifyContent: "center",
                          alignItems: "center",
                        }}
                      >
                        <img
                          src={baseUrlForImg + item.path}
                          alt="productImage"
                          className="productImg"
                        />
                      </div>
                    </Slide>
                  ))}
              </Slider>
            </div>
          </CarouselProvider>
        </div>
        <div className="product__content col s12 m6 l5">
          <h5 className="product__content-title mb-3">
            {product && product.title}
          </h5>
          <h4 className="product__content-price mb-3">
            ${product && product.sellPrice} ./шт
          </h4>

          <span
            onClick={(e) => basketHandler(e)}
            className="waves-effect waves-light btn-large"
            style={{ width: "70%" }}
          >
            В корзину
          </span>
        </div>
      </div>
      <div className="product__description">
        <div className="product__specifications row">
          <h3>Характеристики</h3>
          <table className="striped col s7">
            <tbody>
              {product &&
                product.description.split(",").map((item, index) => (
                  <tr key={index}>
                    <td>{item}</td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Product;
