import React from "react";
import { Carousel } from "react-responsive-carousel";
import Slider from "react-slick";
// import "react-responsive-carousel/lib/styles/carousel.min.css";

const AutoCarousel = () => {
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    initialSlide: 0,
    autoplay: true,
    autoplaySpeed: 2000,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <Slider ref={(slider) => (this.slider = slider)} {...settings}>
      <div>
        <img
          src="https://react-responsive-carousel.js.org/assets/1.jpeg"
          alt="img"
        />
      </div>
      <div>
        <img
          src="https://react-responsive-carousel.js.org/assets/1.jpeg"
          alt="img"
        />
      </div>
      <div>
        <img
          src="https://react-responsive-carousel.js.org/assets/1.jpeg"
          alt="img"
        />
      </div>
      <div>
        <img
          src="https://react-responsive-carousel.js.org/assets/1.jpeg"
          alt="img"
        />
      </div>
      <div>
        <img
          src="https://react-responsive-carousel.js.org/assets/1.jpeg"
          alt="img"
        />
      </div>
      <div>
        <img
          src="https://react-responsive-carousel.js.org/assets/1.jpeg"
          alt="img"
        />
      </div>
    </Slider>
    // <Carousel
    //   autoPlay={true}
    //   infiniteLoop={true}
    //   stopOnHover={true}
    //   showThumbs={false}
    //   className="mb-3"
    //   showArrows={true}
    // >
    //   <div>
    //     <img
    //       src="https://react-responsive-carousel.js.org/assets/1.jpeg"
    //       alt="img"
    //     />
    //   </div>
    //   <div>
    //     <img
    //       src="https://react-responsive-carousel.js.org/assets/1.jpeg"
    //       alt="img"
    //     />
    //   </div>
    //   <div>
    //     <img
