import React from "react";
import axios from "axios";
import { useLocation, useParams } from "react-router-dom";
import "./CatalogProduct.css";
import CatalofProductItem from "../Components/CatalogProductItem";
import Loading from "../Components/Loading/Loading";

const CatalogProduct = () => {
  const [category, setCategory] = React.useState({});
  const [subCategories, setSubCategories] = React.useState(null);

  const { id } = useParams();

  const location = useLocation();

  const useQuery = () => new URLSearchParams(location.search);

  let query = useQuery();

  React.useEffect(() => {
    axios
      .get(`/category/${id}`)
      .then(({ data }) => {
        console.log(data);
        setCategory(data);
        return data;
      })
      .then((data) => {
        axios.get("/subcategory").then((res) => {
          const filtered = res.data.filter(
            (el) => el.category === data.categoryName
          );
        });
        console.log(filtered);
      });
  }, [id]);

  return (
    <div className="container">
      <h1 className="product-content-title">{query.get("q")}</h1>
      <div className="row mb-3">
        {/* {products === null ? (
          <Loading />
        ) : (
          products.map((item, index) => (
            <CatalofProductItem key={index} data={item} />
          ))
        )} */}
      </div>
    </div>
  );
};

export default CatalogProduct;
