import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import AdminSidebar from "../../Components/AdminSidebar";
import "./AdminProductsPage.css";
import Loading from "../../Components/Loading/Loading";

const AdminProductsPage = () => {
  const [products, setProducts] = React.useState([]);
  const [deletedProduct, setDeletedProduct] = React.useState("");

  React.useEffect(() => {
    const { token } = JSON.parse(localStorage.getItem("userData"));

    axios
      .get("/products", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(({ data }) => {
        console.log(data);
        setProducts(data);
      });
    setDeletedProduct("");
  }, [deletedProduct]);

  const deleteHandler = (e, id) => {
    const { token } = JSON.parse(localStorage.getItem("userData"));

    axios
      .delete(`/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(({ data }) => {
        setDeletedProduct(data);
        window.M.toast({ html: "Продукт удалён", classes: "loginTost" });
      });
  };

  return (
    <div className="adminPage">
      <div id="modal1" class="modal modal-fixed-footer">
        <div class="modal-content">
          <h4>Modal Header</h4>
          <p>A bunch of text</p>
        </div>
        <div class="modal-footer">
          <button class="modal-close waves-effect waves-green green btn-flat">
            Agree
          </button>
        </div>
      </div>
      <AdminSidebar />
      <div className="adminProductsPage">
        <div className="adminProductsPageHeader">
          <p className="adminUserPageTitle">Все продукты</p>
          <button
            data-target="modal1"
            class="btn modal-trigger right-align"
            onClick={(e) => {
              const elem = document.querySelectorAll(".modal");
              window.M.Modal.init(elem);
            }}
          >
            Создать продукт
          </button>
        </div>

        <table className="striped">
          <thead>
            <tr>
              <th>№</th>
              <th>Товар</th>
              <th>Название товара</th>
              <th>цена покупки</th>
              <th>цена продажи</th>
              <th>Количество</th>
              <th>Дата выхода товара</th>
              <th></th>
            </tr>
          </thead>

          <tbody>
            {products.length > 0 &&
              products.map((product, index) => (
                <tr key={index + 1}>
                  <th>{index + 1}</th>
                  <th>
                    <img
                      style={{ width: "70px" }}
                      src={`/${product.img[0]?.path}`}
                      alt={product.title}
                    />
                  </th>
                  <th>{product.title}</th>
                  <th>{product.boughtPrice}</th>
                  <th>{product.sellPrice}</th>
                  <th>{product.amount}</th>
                  <th>{new Date(product.createdAt).toDateString()}</th>
                  <th>
                    <Link
                      to={`/admin/products/${product._id}`}
                      className="btn yellow accent-4 waves-effect waves-light"
                    >
                      <i className="fas fa-edit"></i>
                    </Link>
                    <button
                      onClick={(e) => deleteHandler(e, product._id)}
                      className="btn deep-orange darken-1 waves-effect waves-light"
                    >
                      <i className="fas fa-trash"></i>
                    </button>
                  </th>
                </tr>
              ))}
          </tbody>
        </table>
        {products.length === 0 && <Loading />}
      </div>
    </div>
  );
};

export default AdminProductsPage;
