import React from "react";
import "./Basket.css";
import BasketOrderItem from "../Components/BasketOrderItem";
import axios from "axios";
import { baseUrl } from "../baseUrl/baseUrl";

const Basket = () => {
  const [active, setActive] = React.useState("Доставка");
  const [typeUser, setTypeUser] = React.useState("individual");
  const [orders, setOrders] = React.useState([]);
  const [entityOrder, setEntityOrder] = React.useState({
    userId: "",
    products: [],
    stir: "",
    entityName: "",
    phone: "",
    city: "",
    street: "",
    typeSending: "",
  });

  const [userOrder, setUserOrder] = React.useState({
    userId: "",
    products: [],
    phone: "",
    city: "",
    street: "",
  });

  //delete order function
  const deleteHandler = React.useCallback(
    (e, data) => {
      let basket = JSON.parse(localStorage.getItem("salamBasket"));

      if (basket) {
        const filter = basket.filter((item) => item.id !== data._id);
        localStorage.setItem("salamBasket", JSON.stringify(filter));
      }
    },
    [orders]
  );

  //handling input value
  const onChabge = (e) => {
    setEntityOrder({ ...entityOrder, [e.target.name]: e.target.values });
  };

  //radio buttons
  const radioHandler = (e) => {
    setActive(e.target.value);
    setEntityOrder({ ...entityOrder, typeSending: e.target.value });
  };

  //getting orders from localstorage and request
  React.useEffect(() => {
    const basket = JSON.parse(localStorage.getItem("salamBasket"));
    if (basket) {
      const ids = basket.map((item) => item.id);

      axios
        .post(
          baseUrl + "/products/cart",
          { products: ids },
          {
            headers: {
              "Content-Type": "application/json",
            },
          }
        )
        .then((res) => {
          setOrders(res.data);
        });
    }
  }, [deleteHandler]);

  //submit order
  const submitHandler = (e) => {
    console.log(entityOrder);
  };

  return (
    <div className="container">
      <h1>Корзина</h1>
      <div className="basket row ">
        <div className="products col s12 l8">
          <div className="row">
            <div className="col s12 m6 l6">
              <label
                className={`choose__option mr-1 ${
                  active === "Доставка" ? "active" : ""
                }`}
              >
                <input
                  className="with-gap"
                  checked={active === "Доставка" ? true : false}
                  name="group1"
                  type="radio"
                  value={"Доставка"}
                  onChange={(e) => radioHandler(e)}
                />
                <span>Доставка</span>
              </label>
            </div>
            <div className="col s12 m6 l6">
              <label
                className={`choose__option ${
                  active === "Самовывоз" ? "active" : ""
                }`}
              >
                <input
                  className="with-gap"
                  name="group1"
                  type="radio"
                  value={"Самовывоз"}
                  onChange={(e) => radioHandler(e)}
                />
                <span>Самовывоз</span>
              </label>
            </div>
          </div>

          <div className="basket-orders">
            {!orders.length ? (
              <h5 className="empty">Пока в корзине ничего нет</h5>
            ) : (
              orders.map((order, index) => (
                <BasketOrderItem
                  data={order}
                  key={index}
                  deleteHandler={deleteHandler}
                />
              ))
            )}
          </div>
        </div>

        <div className="product-order col s12  l4">
          <div className="form row">
            <div className="form-price">
              <span className="form-price-title">
                Товары <sup>(2)</sup>{" "}
              </span>
              <span className="form-price-number">100000 Р</span>
            </div>
            <div className="form-summ">
              <span className="form-summ-title">Итого</span>
              <span className="form-summ-number">100000 Р</span>
            </div>
            <div className="form-action">
              <button
                className="btn form-btn"
                onClick={(e) => submitHandler(e)}
              >
                Оформить заказ
              </button>
            </div>
          </div>

          <form className="form row">
            <div className="col s12">
              <div className="row">
                <p>
                  <label>
                    <input
                      className="with-gap"
                      name="group1"
                      type="radio"
                      value={"individual"}
                      onChange={(e) => setTypeUser(e.target.value)}
                      checked={typeUser === "individual" ? true : false}
                    />
                    <span>Я физическое лицо</span>
                  </label>
                </p>

                <p>
                  <label>
                    <input
                      className="with-gap"
                      name="group1"
                      type="radio"
                      value={"entity"}
                      onChange={(e) => setTypeUser(e.target.value)}
                    />
                    <span>Я юридическое лицо</span>
                  </label>
                </p>
              </div>

              {/* Я юридическое лицо */}
              {typeUser === "entity" ? (
                <>
                  <div className="row">
                    <div className="input-field col s12">
                      <input
                        id="stir"
                        type="text"
                        className="validate"
                        onChange={(e) => onChabge(e)}
                        value={entityOrder.stir}
                      />
                      <label htmlFor="stir">ИНН организации</label>
                    </div>
                  </div>

                  <div className="row">
                    <div className="input-field col s12">
                      <input
                        id="entityName"
                        type="text"
                        className="validate"
                        onChange={(e) => onChabge(e)}
                        value={entityOrder.entityName}
                      />
                      <label htmlFor="entityName">Название организации</label>
                    </div>
                  </div>

                  <div className="row">
                    <div className="input-field col s12">
                      <input
                        id="phone"
                        type="tel"
                        className="validate"
                        onChange={(e) => onChabge(e)}
                        value={entityOrder.phone}
                      />
                      <label htmlFor="phone">Телефон номер организации</label>
                    </div>
                  </div>

                  <div className="row">
                    <div className="input-field col s12">
                      <select
                        className="browser-default"
                        onChange={(e) => onChabge(e)}
                        value={entityOrder.city}
                      >
                        <option value="" disabled selected>
                          Город
                        </option>
                        <option value="Самаркандская область">
                          Самаркандская область
                        </option>
                        <option value="Ферганская область">
                          Ферганская область
                        </option>
                        <option value="Кашкадарьинская область">
                          Кашкадарьинская область
                        </option>
                        <option value="Андижанская область">
                          Андижанская область
                        </option>
                        <option value="Ташкентская область">
                          Ташкентская область
                        </option>
                        <option value="Наманганская область">
                          Наманганская область
                        </option>
                        <option value="Сурхандарьинская область">
                          Сурхандарьинская область
                        </option>
                        <option value="г.Ташкент">г.Ташкент</option>
                        <option value="Бухарская область">
                          Бухарская область
                        </option>
                        <option value="Хорезмская область">
                          Хорезмская область
                        </option>
                        <option value="Республика Каракалпакстан	">
                          Республика Каракалпакстан{" "}
                        </option>
                        <option value="Джизакская область">
                          Джизакская область
                        </option>
                        <option value="Навоийская область">
                          Навоийская область
                        </option>
                        <option value="Сырдарьинская область">
                          Сырдарьинская область
                        </option>
                      </select>
                    </div>
                  </div>

                  <div className="row">
                    <div className="input-field col s12">
                      <input
                        id="street"
                        type="text"
                        className="validate"
                        onChange={(e) => onChabge(e)}
                        value={entityOrder.street}
                      />
                      <label htmlFor="street">Улица</label>
                    </div>
                  </div>
                </>
              ) : (
                ""
              )}

              {/* Я физическое лицо */}

              {typeUser === "individual" ? (
                <>
                  <div className="row">
                    <div className="input-field col s12">
                      <input id="phone" type="tel" className="validate" />
                      <label htmlFor="phone">Телефон номер</label>
                    </div>
                  </div>

                  <div className="row">
                    <div className="input-field col s12">
                      <select className="browser-default">
                        <option value="" disabled selected>
                          Город
                        </option>
                        <option value="Самаркандская область">
                          Самаркандская область
                        </option>
                        <option value="Ферганская область">
                          Ферганская область
