import React from "react";
import AdminSidebar from "../../Components/AdminSidebar";
import "./AdminUsersPage.css";
import axios from "axios";
import Loading from "../../Components/Loading/Loading";
import { Link } from "react-router-dom";

const AdminSubSubCategoryPage = () => {
  const [subSubCategories, setSubSubCategories] = React.useState([]);
  const [deleted, setDeleted] = React.useState("");

  const deleteHandler = async (e, id) => {
    const { token } = JSON.parse(localStorage.getItem("userData"));

    try {
      if (window.confirm("Вы увепены?")) {
        const { data } = await axios.delete(`/users/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        window.M.toast({
          html: "Ползователь удален",
          classes: "loginToast",
        });
        setDeleted(data.msg + Date.now());
      }
    } catch (e) {
      window.M.toast({ html: e.message, classes: "loginToastRed" });
      return;
    }
  };

  React.useEffect(() => {
    const { token } = JSON.parse(localStorage.getItem("userData"));

    axios
      .get("/subsubcategory", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(({ data }) => {
        setSubSubCategories(data);
      });
  }, [deleted]);

  const submitHandler = (e) => {
    console.log(e);
  };

  return (
    <div className="adminPage">
      <div id="modal1" className="modal modal-fixed-footer">
        <div className="modal-content">
          <h4>Cоздать категорию</h4>
          <form
            onSubmit={(e) => submitHandler(e)}
            style={{ padding: "30px 60px" }}
          >
            <div className="row">
              <div className="input-field col s12">
                <select
                  value={formCategory}
                  onChange={(e) => setFormCategory(e.target.value)}
                  className="browser-default"
                >
                  <option value="" disabled>
                    Категория
                  </option>
                  {category.length > 0 &&
                    category.map((el) => (
                      <option key={el._id} value={el._categoryName}>
                        {el.categoryName}
                      </option>
                    ))}
                </select>
              </div>
            </div>

            <div className="row">
              <div className="input-field col s12">
                <input
                  id="categoryName"
                  type="text"
                  required
                  className="validate"
                  // value={formSubCategory}
                  // onChange={(e) => setFormSubCategory(e.target.value)}
                />
                <label htmlFor="categoryName">Подкатегория</label>
              </div>
            </div>

            <div className="row">
              <div className="file-field input-field col s12">
                <div className="btn">
                  <span>Изображения</span>
                  <input
                    type="file"
                    required
                    onChange={(e) => setImg(e.target.files[0])}
                  />
                </div>
                <div className="file-path-wrapper">
                  <input className="file-path validate" type="text" />
                </div>
              </div>
            </div>

            <div className="row">
              <div className="input-field col s12">
                <button
                  className="btn waves-effect waves-green green btn-flat close-modal"
                  type="submit"
                >
                  Создать
                </button>
              </div>
            </div>
          </form>
        </div>
        <div className="modal-footer"></div>
      </div>
      <AdminSidebar />
      <div className="adminUsersPage">
        <div className="adminProductsPageHeader">
          <p className="adminProductPageTitle">Все подподкатегории</p>
          <button
            data-target="modal1"
            className="btn modal-trigger right-align"
            onClick={(e) => {
              const elem = document.querySelectorAll(".modal");
              window.M.Modal.init(elem);
            }}
          >
            Создать подподкатегорию
          </button>
        </div>

        <table className="striped">
          <thead>
            <tr>
              <th>№</th>
              <th>Подподкатегория</th>
              <th>Подкатегория</th>
              <th>Категория</th>
              <th>Создан</th>
              <th>Изменить</th>
              <th>Удалить</th>
            </tr>
          </thead>

          <tbody>
            {subSubCategories.length > 0 &&
              subSubCategories.map((el, index) => (
                <tr key={index + 1}>
                  <th>{index + 1}</th>
                  <th>{el.subSubCategoryName}</th>
                  <th>{el.subCategory}</th>
                  <th>{el.category}</th>
                  <th>{new Date(el.createdAt).toDateString()}</th>
                  <th>
                    <Link
                      to={`/admin/subsubcategory/${el._id}`}
                      className="btn yellow accent-4 waves-effect waves-light"
                    >
                      <i className="fas fa-edit"></i>
                    </Link>
                  </th>
                  <th>
                    <button
                      onClick={(e) => deleteHandler(e, el._id)}
                      className="btn deep-orange darken-1 waves-effect waves-light"
                    >
                      <i className="fas fa-trash"></i>
                    </button>
                  </th>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminSubSubCategoryPage;
