import React from "react";
import { Link } from "react-router-dom";

const CatalogProductItem = ({ data, ownstyle }) => {
  const basketHandler = (e, datum) => {
    let addedProduct = JSON.parse(localStorage.getItem("salamBasket"));
    // if (addedProduct && !addedProduct.inludes(datum._id)) {
    //   addedProduct.push(datum._id);
    //   localStorage.setItem("salamBasket", JSON.stringify(addedProduct));
    // } else {
    //   localStorage.setItem("salamBasket", JSON.stringify([datum._id]));
    // }

    const arr = [1, 2, 3, 4, 5, 6];

    console.log(addedProduct.includes(datum._id));
  };

  return (
    <div className="col s12 m4" style={ownstyle && ownstyle}>
      <div className="card">
        <div className="card-image">
          <img
            src="https://materializecss.com/images/sample-1.jpg"
            alt="product img"
          />
          <Link
            to={`/product/${data._id}`}
            className="card-title"
            style={{ cursor: "pointer" }}
          >
            {data.title}
          </Link>
        </div>
        <div className="card-content">
          <p>
            I am a very simple card. I am good at containing small bits of
            information. I am convenient because I require little markup to use
            effectively.
          </p>
        </div>
        <div className="card-action">
          <button
            className="btn waves-effect"
            onClick={(e) => basketHandler(e, data)}
          >
            В корзину
          </button>
        </div>
      </div>
    </div>
  );
};

export default CatalogProductItem;
