import React from "react";
import { baseUrl } from "../baseUrl/baseUrl";
import "./BasketOrderItem.css";

const BasketOrderItem = ({ data, deleteHandler, quantityHandler }) => {
  console.log(data);
  return (
    <div className="basket-order mb-2">
      <div className="order-image-box ml-1">
        <img
          className="order-image"
          alt="product img"
          src={baseUrl + `/${data.img[0]}`}
        />
      </div>
      <div className="order-title">
        <p>{data.title}</p>
      </div>
      <div className="order-quantity row">
        {quantityHandler ? (
          <input
            min="1"
            style={{ width: "40px" }}
            type={"number"}
            defaultValue={"1"}
            onChange={(e) => quantityHandler(e, data)}
          />
        ) : (
          <span className="mr-1">1</span>
        )}
        <label>шт.</label>
      </div>
      <div className="order-price">
        <h4>{data.sellPrice}$</h4>
      </div>
      {deleteHandler && (
        <div
          onClick={deleteHandler && ((e) => deleteHandler(e, data))}
          className="order-delete"
        >
          <i className="fas fa-trash-alt delete"></i>
        </div>
      )}
    </div>
  );
};

export default BasketOrderItem;
